import Input from "../UI/Input";
import Card from "../UI/Card";
import classes from "./Signup.module.css";
import { useState } from "react";

const Signup = () => {
  const [userSignupState, setUserSignupState] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const inputChangeHandler = (id, value) => {
    setUserSignupState((state) => {
      return { ...state, [id]: value };
    });
  };

  const signupSubmitHandler = (event) => {
    event.preventDefault();

    setUserSignupState({
      name: "",
      email: "",
      password: "",
      confirmPassword: "",
    });
  };

  return (
    <Card className={classes.form}>
      <form onSubmit={signupSubmitHandler}>
        <h3 style={{ textAlign: "center", fontWeight: "bold" }}>Sign Up</h3>
        <div style={{display: "flex", flexDirection: 'row', justifyContent: 'space-between'}}>
          <Input
            id="firstname"
            label="Name"
            type="text"
            value={userSignupState.firstName}
            onChange={inputChangeHandler}
          />
          <Input
            id="lastName"
            type="text"
            value={userSignupState.lastName}
            onChange={inputChangeHandler}
          />
        </div>
        <Input
          id="email"
          label="Email"
          type="email"
          value={userSignupState.email}
          onChange={inputChangeHandler}
        />
        <Input
          id="password"
          label="Password"
          type="password"
          value={userSignupState.password}
          onChange={inputChangeHandler}
        />
        <Input
          id="confirmPassword"
          label="Confirm Password"
          type="password"
          value={userSignupState.confirmPassword}
          onChange={inputChangeHandler}
        />
        <div className={classes.actions}>
          <button type="submit" className={classes.btn}>
            Sign up
          </button>
        </div>
      </form>
    </Card>
  );
};

export default Signup;
