import React, { useContext } from "react";
import AuthContext from "../../context/auth-context";
import classes from "./Navbar.module.css";

function Navbar() {
  const authContext = useContext(AuthContext);
  const NavbarLinks = (
    <div>
      <a href="/addAuthor" style={{ textDecoration: "none", color: "black" }}>
        <div className={classes["navbar-item"]}>Add author</div>
      </a>

      <a href="/showBooks" style={{ textDecoration: "none", color: "black" }}>
        <div className={classes["navbar-item"]}>Book list</div>
      </a>
      <a href="/checkout" style={{ textDecoration: "none", color: "black" }}>
        <div className={classes["navbar-item"]}>Checkout</div>
      </a>
    </div>
  );
  return (
    <nav style={{ backgroundColor: "lightblue" }}>
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <h1 style={{ margin: 0, padding: "10px" }}>
          Library Management Systems
        </h1>
        <div>
          {!authContext.isLoggedIn && NavbarLinks}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
