import React, { useContext } from 'react';
import AuthContext from '../context/auth-context';
import classes from './Navbar.module.css'

function Navbar() {
  const authContext = useContext(AuthContext);
  return (
    <nav style={{ backgroundColor: 'lightblue'}}>
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <h1 style={{ margin: 0, padding: '10px' }}>Library Management Systems</h1>
        <div>
          {authContext.isLoggedIn && <a href="/addAuthor" style={{ textDecoration: 'none', color: 'black' }}>
            <div
              className={classes['navbar-item']}>
              Add author
            </div>
          </a>}
          <a href="/showBooks" style={{ textDecoration: 'none', color: 'black' }}>
            <div
              style={{ display: 'inline-block', padding: '10px', borderRadius: '10px', transition: 'background-color 0.3s' }}
              onMouseEnter={(e) => e.target.style.backgroundColor = 'skyblue'}
              onMouseLeave={(e) => e.target.style.backgroundColor = 'transparent'}
            >
              Book list
            </div>
          </a>
          <a href="/checkout" style={{ textDecoration: 'none', color: 'black' }}>
            <div
              style={{ display: 'inline-block', padding: '10px', borderRadius: '10px', transition: 'background-color 0.3s' }}
              onMouseEnter={(e) => e.target.style.backgroundColor = 'skyblue'}
              onMouseLeave={(e) => e.target.style.backgroundColor = 'transparent'}
            >
              Checkout
            </div>
          </a>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
