import * as React from 'react';
import { Button } from 'react-bootstrap';

export default function LoginPage() {
    const [username, setUsername] = React.useState("");
    const [password, setPassword] = React.useState("");
    return (
        <div style={{width: '100%', height: '100%', display: 'flex', justifyContent: 'center'}}>
            <div id='centerDiv'>
                <h1 style={{textAlign: 'center', paddingBottom: '30px'}}>Library Management System</h1>
                <form onSubmit={(e) => e.preventDefault()}>
                    <label><b>Username</b></label><br></br>
                    <input type='text'placeholder='Enter Username' className='inputBox' value={username} onChange={(e) => setUsername(e.target.value)}/><br></br>
                    <label><b>Password</b></label><br></br>
                    <input type='password' placeholder='Enter password' className='inputBox' value={password} onChange={(e) => setPassword(e.target.value)}></input>
                    <Button onClick={login} id='loginButton'>Login</Button>
                </form>
                <div className='newUserContainer' style={{marginTop: '10px'}}>
                    <span className='forgotPassword'><a href = '/login' onClick={forgotPassword}>Forgot Password</a></span>
                    <span className='newUser' style={{float: 'right'}}><Button onClick={newUser}>New User</Button></span>
                </div>
                <div className='SSOLogin' style={{marginTop: '20px'}}>
                    <span style={{float: 'right', width: '50%', marginLeft: '10px'}}><Button style={{width: '100%'}}>Login with Google</Button></span>
                    <span style={{float: 'left', width: '50%'}}><Button style={{width: '100%'}}>Login with Apple</Button></span>
                </div>
            </div>
        </div>
    );

    function login() {};
    function forgotPassword() {
        console.log("Forgot Password");
    };
    function newUser() {};
}
