import * as React from 'react';
// import { TableRow, TableHead, TableContainer, TableCell, TableBody, Table, Paper, Chip } from '@material-ui/core';

function BookTable(props) {
  const rows = props.books;
  return (
    <>
    <h2 className="header">List of available books</h2>
    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Book Name&nbsp;</TableCell>
            <TableCell align="right">Author Names&nbsp;</TableCell>
            <TableCell align="right">Number of books available&nbsp;</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow
              key={row.bookID}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                {row.bookName}
              </TableCell>
              <TableCell align='right'>{row.authorNames.map((author) => {
                  return <Chip key={author.authorID} label={author.authorName} />
              })}</TableCell>
              <TableCell align="right">{row.numberOfBooks}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
    </>
  );
}

export default BookTable;
