import * as React from 'react';
import { styled } from '@mui/material/styles';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell, { tableCellClasses } from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import Chip from '@mui/material/Chip';
import { useEffect, useState } from "react";

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  '&:last-child td, &:last-child th': {
    border: 0,
  },
}));

export default function BookTable1() {
  const [bookList, setBookList] = useState([]);

  useEffect(() => {
    const fetchData = () => {
      fetch("http://localhost:8080/demo/listBooks")
        .then(async (res) => {
          const data = await res.json();
          setBookList(data);
        })
        .catch((err) => {
          console.log(err.message);
        });
    };
    fetchData();
  }, []);

  const rows = bookList;
  return (<>
    <h2 style={{margin: '10px', display: 'flex', justifyContent: 'center'}}>List of available books</h2>
    <div style={{padding: '10px'}}>
      <TableContainer component={Paper} style={{borderRadius: '10px', height: '500px'}}>
        <Table sx={{ minWidth: 700 }} aria-label="customized table" style={{position: 'stickey', top: 0}}>
          <TableHead>
            <TableRow>
              <StyledTableCell>Book name</StyledTableCell>
              <StyledTableCell align="right">Author Names</StyledTableCell>
              <StyledTableCell align="right">Number of books available&nbsp;</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row) => (
              <StyledTableRow key={row.bookName}>
                <StyledTableCell component="th" scope="row">
                  {row.bookName}
                </StyledTableCell>
                <StyledTableCell align="right">{row.authorNames.map((author) => {
                    return <Chip style={{marginRight: '5px'}} key={author.authorID} label={author.authorName} />
                })}</StyledTableCell>
                <StyledTableCell align="right">{row.numberOfBooks}</StyledTableCell>
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
    </>
  );
}
