import React, { useEffect, useState } from "react";
import Button from "react-bootstrap/Button";
import "./App.css";
import BookTable1 from "./BookTable1";
import NavigationBar from "./NavigationBar";

function App() {
  const [bookList, setBookList] = useState([]);
  const [bookName, setBookName] = useState("");
  const [numberOfBooks, setNumberOfBooks] = useState(0);
  const [authors, setAuthors] = useState([
    {
      authorName: "",
    },
  ]);
  const [bookAdded, setBookAdded] = useState(1);

  useEffect(() => {
    const fetchData = () => {
      fetch("http://localhost:8080/demo/listBooks")
        .then(async (res) => {
          const data = await res.json();
          setBookList(data);
        })
        .catch((err) => {
          console.log(err.message);
        });
    };
    fetchData();
  }, [bookAdded]);

  return (
    <div className="App">
      <NavigationBar/>
      <form onSubmit={(e) => e.preventDefault()}>
        <label className="label">Book name: </label>
        <input
          type="text"
          className="input input_lg bookInputBox"
          id="bookName"
          value={bookName}
          onChange={(e) => setBookName(e.target.value)}
        ></input>
        <br></br>
        {authors.map((author, index) => (
          <div key={index}>
            <label className="label">Author name: </label>
            <input
              type="text"
              className="input input_lg bookInputBox"
              id="authorNames"
              value={author.authorName}
              onChange={(e) => {
                let newAuthors = [...authors];
                newAuthors[index].authorName = e.target.value;
                setAuthors(newAuthors);
              }}
            ></input>
          </div>
        ))}
        <Button
          id="addAuthorButton"
          className="btn btn-primary btn-lg"
          onClick={addAuthor}
        >
          +
        </Button>
        <br></br>
        <label className="label">Number of books: </label>
        <input
          type="text"
          className="input input_lg bookInputBox"
          id="numberOfBooks"
          value={numberOfBooks}
          onChange={(e) => setNumberOfBooks(e.target.value)}
        ></input>
        <br></br>
        <Button
          type="submit"
          className="btn btn-primary btn-lg addButton"
          onClick={addToList}
        >
          Add book
        </Button>
      </form>
      <BookTable1 books={bookList} />
    </div>
  );

  function addToList() {
    fetch("http://localhost:8080/demo/addBook", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        bookName: bookName,
        authorNames: authors,
        numberOfBooks: numberOfBooks,
      }),
    }).then(async res => {
      console.log(await res.json());
    })
    .catch((err) => {
      console.log(err.message);
    });
    setBookAdded(bookAdded + 1);
    setBookName("");
    setAuthors([{ authorName: "" }]);
    setNumberOfBooks(0);
  }

  function addAuthor() {
    setAuthors([...authors, { authorName: "" }]);
  }
}

export default App;
