import Input from "../UI/Input";
import Card from "../UI/Card";
import classes from "./Signup.module.css";
import { useState } from "react";

const Signup = () => {
  const [userSignupState, setUserSignupState] = useState({
    email: '',
    password: '',
    confirmPassword: ''
  });

  const inputChangeHandler = (id, value) => {
    setUserSignupState((state) => {
      return {...state, [id]: value}
    });
  }

  const signupSubmitHandler = (event) => {
    event.preventDefault();
    console.log(userSignupState);
  }

  return (
    <Card className={classes.form}>
      <form onSubmit={signupSubmitHandler}>
        <Input id="email" label="Email" type="email" onChange={inputChangeHandler}/>
        <Input id="password" label="Password" type="password" onChange={inputChangeHandler}/>
        <Input id="confirmPassword" label="Confirm Password" type="password" onChange={inputChangeHandler}/>
        <div className={classes.actions}>
          <button type="submit" className={classes.btn}>
            Sign up
          </button>
        </div>
      </form>
    </Card>
  );
};

export default Signup;
