import * as React from "react";
import { styled } from "@mui/material/styles";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell, { tableCellClasses } from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import Chip from "@mui/material/Chip";
import { useEffect, useState } from "react";
import Button from "react-bootstrap/Button";
import BookForm from "./BookForm";

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  "&:nth-of-type(odd)": {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  "&:last-child td, &:last-child th": {
    border: 0,
  },
}));

export default function BookTable() {
  const [bookList, setBookList] = useState([]);

  useEffect(() => {
    const fetchData = () => {
      fetch("http://localhost:8080/demo/listBooks")
        .then(async (res) => {
          const data = await res.json();
          setBookList(data);
        })
        .catch((err) => {
          console.log(err.message);
        });
    };
    fetchData();
  }, []);

  const bookNumberChangeHandler = (id, value) => {
    console.log("On Change");
    return;
  };

  return (
    <>
      <h2 style={{ margin: "10px", display: "flex", justifyContent: "center" }}>
        List of available books
      </h2>
      <div style={{ padding: "10px" }}>
        <TableContainer
          component={Paper}
          style={{
            borderRadius: "10px",
            height: "800px",
            overflowY: "auto",
            boxShadow: "0px 2px 8px rgba(0, 0, 0, 0.26",
          }}
        >
          <Table sx={{ minWidth: 700 }} aria-label="customized table">
            <TableHead style={{ position: "sticky", top: 0 }}>
              <TableRow>
                <StyledTableCell>Book name</StyledTableCell>
                <StyledTableCell align="right">Author Names</StyledTableCell>
                <StyledTableCell align="right">
                  Number of books available&nbsp;
                </StyledTableCell>
                <StyledTableCell align="right">
                  Number of books to checkout&nbsp;
                </StyledTableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {bookList.map((row) => (
                <StyledTableRow key={row.bookID}>
                  <StyledTableCell component="th" scope="row">
                    {row.bookName}
                  </StyledTableCell>
                  <StyledTableCell align="right">
                    {row.authorNames.map((author) => {
                      return (
                        <Chip
                          style={{ marginRight: "5px" }}
                          key={author.authorID}
                          label={author.authorName}
                        />
                      );
                    })}
                  </StyledTableCell>
                  <StyledTableCell align="right">
                    {row.numberOfBooks}
                  </StyledTableCell>
                  <StyledTableCell align="right">
                    <BookForm
                      id={row.bookID}
                      onBlur={bookNumberChangeHandler}
                    />
                  </StyledTableCell>
                </StyledTableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </div>
      <div
        className="addBooksToCheckout"
        style={{ display: "flex", justifyContent: "center" }}
      >
        <Button id="addBooksToCheckout" style={{ padding: "10px" }}>
          Add Books
        </Button>
      </div>
    </>
  );
}

function bookSelected() {
  console.log("Number changed");
}
