import { createContext, useState } from "react";

const CartContext = createContext({
  bookList: [],
  totalQuantity: 0,
  addBook: (book) => {},
  removeBook: (id) => {},
});

export default CartContext;

export const CartProvider = (props) => {
  const [cartState, setCartState] = useState([]);
  const [totalBooks, setTotalBooks] = useState(0);
  const addBookHandler = (newBook) => {
    setCartState((state) => {
        const currentBooks = state;
        const bookIndex = state.findIndex((book) => book.id === newBook.id);
        let updatedBook = state[bookIndex];
        updatedBook.quantity += newBook.quantity;
        currentBooks[bookIndex] = updatedBook;
        return currentBooks;
    });
    setTotalBooks((state) => {
        return state + book.quantity;
    });
  };
  const removeBookHandler = (id) => {};
  return (
    <CartContext.Provider
      value={{
        bookList: cartState,
        totalQuantity: totalBooks,
        addBook: addBookHandler,
        removeBook: removeBookHandler,
      }}
    >{props.children}</CartContext.Provider>
  );
};
