import React, { useContext } from "react";
import AuthContext from "../../context/auth-context";
import classes from "./Navbar.module.css";
import Button from "../UI/Button";
import { Link } from "react-router-dom";
import CartContext from "../../context/cart-context";

function Navbar() {
  const authContext = useContext(AuthContext);
  const cartContext = useContext(CartContext);
  const NavbarLinks = (
    <div>
      {authContext.userRole === "ADMIN" && (
        <Link
          to="/addAuthor"
          style={{ textDecoration: "none", color: "black" }}
        >
          <div className={classes["navbar-item"]}>Add author</div>
        </Link>
      )}
      <Link to="/showBooks" style={{ textDecoration: "none", color: "black" }}>
        <div className={classes["navbar-item"]}>Book list</div>
      </Link>
      <Link to="/checkout" style={{ textDecoration: "none", color: "black" }}>
        <div className={classes["navbar-item"]} >
          <span>Checkout</span>
          <div className={classes.badge}> {cartContext.totalQuantity}</div>
        </div>
      </Link>
      <Button
        className={classes.btn}
        type="button"
        onClick={authContext.onLogout}
      >
        Logout
      </Button>
    </div>
  );
  return (
    <nav style={{ backgroundColor: "lightblue" }}>
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <h2 style={{ margin: 0, padding: "10px" }}>
          Library Management Systems
        </h2>
        <div>{authContext.isLoggedIn && NavbarLinks}</div>
      </div>
    </nav>
  );
}

export default Navbar;
