import { forwardRef } from "react";
import classes from "./BookForm.module.css";

const BookForm = forwardRef((props, ref) => {
  return (
    <>
      <input
        className={`${classes.input} ${props.invalidInput ? classes.invalid : ''}`}
        ref={ref}
        type="number"
        min="0"
        max={props.numOfBooks}
        step={1}
        onBlur={(event) => {
          props.onBlur(props.id, event.target.value, props.numOfBooks);
        }}
      />
    </>
  );
});

export default BookForm;
