import React, { useState } from 'react';
import './LoginPage.css'; // Import the CSS file for styling

function LoginPage() {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');

  const handleUserIdChange = (e) => {
    setUserId(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    // Perform user ID and password authentication logic
    // Redirect the user to the dashboard or home page upon successful authentication
    // history.push('/dashboard');
  };

  const handleGoogleSignIn = () => {
    // Perform Google sign-in logic
  };

  const handleAppleSignIn = () => {
    // Perform Apple sign-in logic
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleFormSubmit}>
        <label htmlFor="userId">User ID:</label>
        <input type="text" id="userId" value={userId} onChange={handleUserIdChange} />

        <label htmlFor="password">Password:</label>
        <input type="password" id="password" value={password} onChange={handlePasswordChange} />

        <button type="submit">Login</button>
      </form>

      <div className="oauth-buttons">
        <button className="google-button" onClick={handleGoogleSignIn}>
          Sign in with Google
        </button>
        <button className="apple-button" onClick={handleAppleSignIn}>
          Sign in with Apple
        </button>
      </div>
    </div>
  );
}

export default LoginPage;
